/* Multi-Columns */

.flow{
  flex: 1;           /* nimmt die Resthöhe unter dem Header */
  min-height: 0;     /* wichtig in Flex-Layouts, sonst Overflow-Bugs */
  overflow: hidden;  /* bleibt: verhindert "unendlich nach unten" */

  box-sizing: border-box;
  margin: 0 var(--side-pad);
