  column-gap: var(--gap) !important;
  -webkit-column-gap: var(--gap) !important;

  column-width: auto !important;
  -webkit-column-width: auto !important;

  column-fill: auto !important;  /* links->rechts füllen, nicht "balance" */
}


@media print{
